import { useState } from "react";
import styles from "../App.module.css";

function CalorieTracker() {
  const [calories, setCalories] = useState(0);
  const [goal, setGoal] = useState(2000);
  const [food, setFood] = useState("");
  const [log, setLog] = useState([]);

  const addCalories = () => {
    if (!food) return;
    const newLog = [...log, { name: food, calories: Math.floor(Math.random() * 300) }];
    setLog(newLog);
    setCalories(newLog.reduce((sum, item) => sum + item.calories, 0));
    setFood("");
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <button className={styles.menuButton}>â˜°</button>
        <h1 className={styles.title}>Calorie Tracker</h1>
      </div>
      <div className={styles.progressBarContainer}>
        <div 
          className={styles.progressBar} 
          style={{ width: `${(calories / goal) * 100}%` }}
        ></div>
      </div>
      <p className={styles.calorieInfo}>{calories} / {goal} kcal</p>
      <div className={styles.inputContainer}>
        <input
          type="text"
          placeholder="Enter food"
          value={food}
          onChange={(e) => setFood(e.target.value)}
          className={styles.input}
        />
        <button onClick={addCalories} className={styles.addButton}>Add</button>
      </div>
      <ul className={styles.foodList}>
        {log.map((item, index) => (
          <li key={index} className={styles.foodItem}>
            <span>{item.name}</span>
            <span>{item.calories} kcal</span>
          </li>
        ))}
      </ul>
    </div>
  );
}


export default CalorieTracker
